# Iterate through SampleName folders in the main directory
for sample_dir in "$main_directory"/*/; do
    # Find the denovo folder within the SampleName folder
    denovo_dir=$(find "$sample_dir" -type d -name "denovo_*" -print -quit)
    
    # Check if the denovo folder exists
    if [ -n "$denovo_dir" ]; then
        # Get the SampleName from the directory path
        sample_name=$(basename "$sample_dir")

        # Iterate through outTable_* files in the denovo directory
        for out_table_file in "$denovo_dir"/outTable_*; do
            # Check if it's a file
            if [ -f "$out_table_file" ]; then
                # Echo the path of the current out_table_file
                echo "*outTable To Be Copied:* $out_table_file"
                
                # Extract the file number from the outTable_* filename
                file_number=$(echo "$out_table_file" | grep -oP 'outTable_\K\d+')

                # Define the new filename
                new_filename="${sample_name}_outTable_${file_number}"

                # Copy the file to the output directory with the new name
                cp "$out_table_file" "$output_directory/$new_filename"

                # Echo the path of the new file
                echo "*File Created:* $output_directory/$new_filename"
            fi
        done
    fi
