# Run VEP command
##Fasta file should ideally be regular not masked that is required
## Removed the most severe flag
##   --most_severe

singularity exec $singularity_image $entrypoint vep \
  --cache \
  -dir_cache $CACHE_DIR \
  --fasta $FASTA_FILE \
  --assembly $ASSEMBLY \
  -cache_version $CACHE_VERSION \
  --merged \
  --offline \
  --species $SPECIES \
  --gtf $GTF_FILE \
  --input_file $INPUT_FILE \
  --format vcf \
  --tab \
  --fields "Uploaded_variation,Location,Allele,Consequence,Impact,Symbol,Gene,Feature_type,Feature,Biotype,cDNA_position,CDS_position,Protein_position,Amino_acids,Codons,Existing_variation,Extra" \
  --everything \
  --output_file $OUTPUT_FILE \
  --verbose

