## Section 4: Main

### Check if the script file exists
if [ -f "$RSCRIPT_PATH" ]; then
  ### Print the script path
  echo -e "*Rscript Path:* $RSCRIPT_PATH\n\n"

  ### Extract the directory from the full path
  RSCRIPT_DIR=$(dirname "$RSCRIPT_PATH")

  ### Change to the specified directory
  cd "$RSCRIPT_DIR"

  ### Run your R script
  Rscript "$(basename "$RSCRIPT_PATH")"
else
  ### Exit if the script file does not exist
  echo "RScript file does not exist. Exiting program."
  exit 1
fi

